var popup={bg:chrome.extension.getBackgroundPage()};$(function(){popup.$playBtn=$("#btn-play");popup.$volumeInp=$("#inp-volume");popup.$nowPlaying=$("#now-playing");if(popup.bg.keuneksen.isPlaying){popup.bg.keuneksen.setTexts();popup.$playBtn.attr("class",popup.bg.keuneksen.isPlaying)}sendMessage({type:"query"},function(a){if(a.volume){popup.$volumeInp.val(a.volume)}});bindEvents()});function sendMessage(b,a){chrome.extension.sendMessage(b,function(c){if(a){a(c)}})}function bindEvents(){popup.$playBtn.click(function(){var a=$(this);var b={type:"set"};if(popup.bg.keuneksen.isPlaying==="yes"){a.attr("class","");b.isPlaying="no"}else{a.attr("class","yes");b.isPlaying="yes"}sendMessage(b)});popup.$volumeInp.change(function(){var a=$(this).val();var b={type:"set",volume:a};sendMessage(b)})}function setNowPlaying(a){var b;if(a.length>40){b="11px"}else{b="13px"}popup.$nowPlaying.text(a).attr("title",a).css("font-size",b)}chrome.extension.onMessage.addListener(function(c,b,a){if(c.nowPlaying){setNowPlaying(c.nowPlaying)}});